mapper.select:
SELECT *
FROM (
         SELECT
             ROWNUM R, TT.*
         FROM TB_PRODUCT TT
         WHERE DELETE_YN = 'N'
           AND TITLE LIKE '%' || #{title} || '%'
           AND ROWNUM <= (#{page} + 1) * #{size}
     )
WHERE R > #{page} * #{size}

SELECT *
FROM (
         SELECT ROWNUM R, TT.*
         FROM TB_PRODUCT TT
         WHERE DELETE_YN = 'N'
           AND CATEGORY = #{catId}
           AND ROWNUM <= (#{page} + 1) * #{size}
     )
WHERE R > #{page} * #{size}

SELECT *
FROM (
         SELECT ROWNUM R, TT.*
         FROM TB_PRODUCT TT
         WHERE DELETE_YN = 'N'
           AND SALE_YN = 'Y'
           AND ROWNUM <= (#{page} + 1) * #{size}
     )
WHERE R > #{page} * #{size}
