mapper.insert:
INSERT INTO TB_USER( ID
                   , EMAIL
                   , PASSWORD
                   , USERNAME
                   , ROLE
                   , NAME
                   , ADDRESS
                   , PHONE
                   , INSERT_TIME)
VALUES ( SQ_USER.NEXTVAL
       , #{email}
       , #{password}
       , #{username}
       , #{role}
       , #{name}
       , #{address}
       , #{phone}
       , TO_CHAR(SYSDATE + 9 / 24, 'YYYY-MM-DD HH24:MI:SS'))

mapper.update:
UPDATE TB_USER
SET EMAIL = #{email}
  , PASSWORD = #{password}
  , ADDRESS = #{address}
  , PHONE = #{phone}
  , UPDATE_TIME = TO_CHAR(SYSDATE + 9 / 24, 'YYYY-MM-DD HH24:MI:SS')
WHERE USERNAME = #{username}
  AND DELETE_YN = 'N'
